import axios from "axios"

export const get_user = async(action) => {
    return axios.get("http://localhost:3001/posts").then((res)=>{
        return {
            data: res.data,
            status: res.status
        }
    }).catch((error)=>{
        console.log(error);
    })
}

export const post_user = async(action) => {
    return axios.post("http://localhost:3001/posts").then((res)=>{
        return {
            data: res.data,
            status: res.status
        }
    }).catch((error)=>{
        console.log(error);
    })
}
